  --shadow-2xs: 0px 1px 2px 0px hsl(210 10% 2% / 0.20);
  --shadow-xs: 0px 1px 3px 0px hsl(210 10% 2% / 0.30);
  --shadow-sm: 0px 2px 4px -1px hsl(210 10% 2% / 0.24), 0px 1px 2px -1px hsl(210 10% 2% / 0.16);
  --shadow: 0px 4px 6px -2px hsl(210 10% 2% / 0.20), 0px 2px 4px -1px hsl(210 10% 2% / 0.12);
  --shadow-md: 0px 8px 10px -4px hsl(210 10% 2% / 0.16), 0px 4px 6px -2px hsl(210 10% 2% / 0.20);
  --shadow-lg: 0px 16px 24px -8px hsl(210 10% 2% / 0.32), 0px 8px 12px -4px hsl(210 10% 2% / 0.16);
  --shadow-xl: 0px 24px 48px -12px hsl(210 10% 2% / 0.48), 0px 12px 24px -6px hsl(210 10% 2% / 0.24);
  --shadow-2xl: 0px 48px 96px -24px hsl(210 10% 2% / 0.64);

